import React, { useEffect, useState } from "react";
import axios from 'axios'

import {
  Grid,
  LinearProgress,
  Select,
  OutlinedInput,
  MenuItem,
  Button,
  Divider
} from "@material-ui/core";
import { useTheme } from "@material-ui/styles";
import {
  ResponsiveContainer,
  ComposedChart,
  AreaChart,
  LineChart,
  Line,
  Area,
  PieChart,
  Pie,
  Cell,
  YAxis,
  XAxis,
  Legend,
  Tooltip,
  BarChart,
  Bar
} from "recharts";
// styles
import useStyles from "./styles";

// components
import mock from "./mock";
import Widget from "../../components/Widget/Widget";
import PageTitle from "../../components/PageTitle/PageTitle";
import { Typography } from "../../components/Wrappers/Wrappers";
import Dot from "../../components/Sidebar/components/Dot";
import Table from "./components/Table/Table";
import BigStat from "./components/BigStat/BigStat";

const mainChartData = getMainChartData();
const ProductChartData = getProductBurnData();
const ClientChartData = getClientBurnData();

let PieChartData = [
  { name: "Internally Reported", value: 0, color: "warning" },
  { name: "Customer Reported", value: 0, color: "primary" },
];



export default function QA_Dashboard(props) {
// Setting constants for QA Data
const [closedIssues,setClosedIssues] = useState([])
const [openIssues,setOpenIssues] = useState([])
const [createdIssues,setCreatedIssues] = useState([])
const [changesIssues,setChangesIssues] = useState([])
const [janIssues,setJanIssues] = useState([])
const [febIssues,setFebIssues] = useState([])
const [cusReportBugs,setCusReportBugs] = useState([])



  PieChartData[1].value = cusReportBugs.total_count
  PieChartData[0].value = createdIssues.total_count - cusReportBugs.total_count


  var classes = useStyles();
  var theme = useTheme();

  // local
  var [mainChartState, setMainChartState] = useState("engineer");
  var [ClientChartState, setClientChartState] = useState("YTD");

  return (
    <>
      <PageTitle title="Security"/>
      <Grid container spacing={6}>
        <Grid item lg={3} md={8} sm={6} xs={12}>
          <Widget
            title="Audit"
            upperTitle
            className={classes.card}
          >
          <Divider  />
          <div>
            <Typography>PCI DSS</Typography>
            <Typography color="success">Completed</Typography>
            <Typography>Due: March 2021</Typography>
            <br/>
            <Typography>SOC2 Type II</Typography>
            <Typography weight="bold">In Progress</Typography>
            <Typography>Due: June 2021</Typography>
          </div>
          </Widget>
        </Grid>
        <Grid item lg={3} md={8} sm={6} xs={12}>
          <Widget
            title="Policy Status"
            upperTitle
            className={classes.card}
            bodyClass={classes.fullHeightBody}
          >
          <Divider  />
          <div>
            <Typography>Data Classification</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>Data Protection</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>Password</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>Disaster Recovery</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>PCI</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>SOC</Typography>
            <Typography color="success">Compliant</Typography>
            <br/>

            <Typography>UAC</Typography>
            <Typography color="success">Compliant</Typography>
          </div>
          </Widget>

        </Grid>

        <Grid item xs={12}>
          <Widget
            bodyClass={classes.mainChartBody}
            header={
              <div className={classes.mainChartHeader}>
                <Typography
                  variant="h5"
                  color="text"
                  colorBrightness="secondary"
                >
                  MK System Vulnerability Stats
                </Typography>
                {(mainChartState === "engineer")?
                <div className={classes.mainChartHeaderLabels}>
                  <div className={classes.mainChartHeaderLabel}>
                    <Dot color="warning" />
                    <Typography className={classes.mainChartLegentElement}>
                    Fulfillment
                    </Typography>
                  </div>
                  <div className={classes.mainChartHeaderLabel}>
                    <Dot color="primary" />
                    <Typography className={classes.mainChartLegentElement}>
                      Dev
                    </Typography>
                  </div>
                </div>:
                <div className={classes.mainChartHeaderLabels}>
                <div className={classes.mainChartHeaderLabel}>
                  <Typography className={classes.mainChartLegentElement}>
                    Product
                  </Typography>
                </div>
                </div>}
                <Select
                  value={mainChartState}
                  onChange={e => setMainChartState(e.target.value)}
                  input={
                    <OutlinedInput
                      labelWidth={0}
                      classes={{
                        notchedOutline: classes.mainChartSelectRoot,
                        input: classes.mainChartSelect,
                      }}
                    />
                  }
                  autoWidth
                >
                  <MenuItem value="engineer">Engineer</MenuItem>
                  {/* <MenuItem value="weekly">Client</MenuItem> */}
                  <MenuItem value="product">Product</MenuItem>
                </Select>
              </div>
            }
          >
            {(mainChartState === "engineer")?
            <ResponsiveContainer width="100%" minWidth={500} height={350}>
              <PieChart
                margin={{ top: 0, right: -15, left: -15, bottom: 0 }}
                data={mainChartData}
              >
                <Pie data={mainChartData} dataKey="">

                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
            :             
            <ResponsiveContainer width="100%" minWidth={500} height={350}>
            <BarChart
              margin={{ top: 0, right: -15, left: -15, bottom: 0 }}
              data={ProductChartData}
            >
              <YAxis
                ticks={[0, 5, 10, 15, 20, 25]}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <XAxis
              dataKey = 'product'
                tickFormatter={i => i}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <Tooltip />
              <Bar
                dataKey="Jan"
                fill="#1096CB"  
              />
              <Bar
                dataKey="Feb"
                fill="#FF5C93"  
              />
              <Bar
                dataKey="Mar"
                fill= "#3CD4A0"  
              />

            </BarChart>
          </ResponsiveContainer>}
          </Widget>
        </Grid>
        {/* <Grid item xs={12}>
          <Widget
            title="Client Monthly Stats"
            upperTitle
            noBodyPadding
            bodyClass={classes.tableWidget}
          >
            <Table data={mock.table} />
          </Widget>
        </Grid> */}
        <Grid item xs={12}>
          <Widget
            bodyClass={classes.mainChartBody}
            header={
              <div className={classes.mainChartHeader}>
                <Typography
                  variant="h5"
                  color="text"
                  colorBrightness="secondary"
                >
                  System Update Tracker
                </Typography>
                <div className={classes.mainChartHeaderLabels}>
                <div className={classes.mainChartHeaderLabel}>
                  <Typography className={classes.mainChartLegentElement}>
                    Created vs Implemented
                  </Typography>
                </div>
                </div>
                <Select
                  value={ClientChartState}
                  onChange={e => setClientChartState(e.target.value)}
                  input={
                    <OutlinedInput
                      labelWidth={0}
                      classes={{
                        notchedOutline: classes.mainChartSelectRoot,
                        input: classes.mainChartSelect,
                      }}
                    />
                  }
                  autoWidth
                >
                  <MenuItem value="YTD">Issues</MenuItem>
                  <MenuItem value="burn">Burn Time</MenuItem>
                </Select>
              </div>
            }
          >
            {(ClientChartState === "YTD")?
            <ResponsiveContainer width="100%" minWidth={500} height={350}>
            <BarChart
              margin={{ top: 0, right: -15, left: -15, bottom: 0 }}
              data={ClientChartData}
            >
              <YAxis
                ticks={[0, 15, 25, 35, 45]}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <XAxis
              dataKey = 'stores'
                tickFormatter={i => i}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <Tooltip />
              <Bar
                dataKey="Jan"
                fill="#1096CB"  
              />
              <Bar
                dataKey="Feb"
                fill="#FF5C93"  
              />
              <Bar
                dataKey="Mar"
                fill= "#3CD4A0"  
              />

            </BarChart>
          </ResponsiveContainer>
            :             
            <ResponsiveContainer width="100%" minWidth={500} height={350}>
            <BarChart
              margin={{ top: 0, right: -15, left: -15, bottom: 0 }}
              data={ClientChartData}
            >
              <YAxis
                ticks={[0, 25, 50, 75, 100,125, 150]}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <XAxis
              dataKey = 'stores'
                tickFormatter={i => i}
                tick={{ fill: theme.palette.text.primary + "80", fontSize: 14 }}
                stroke={theme.palette.text.primary + "80"}
              />
              <Tooltip />
              <Bar
                dataKey="JanBurn"
                fill="#1096CB"  
              />
              <Bar
                dataKey="FebBurn"
                fill="#FF5C93"  
              />
              <Bar
                dataKey="MarBurn"
                fill= "#3CD4A0"  
              />

            </BarChart>
          </ResponsiveContainer>}
          </Widget>
        </Grid>
      </Grid>
    </>
  );
}

// #######################################################################
function getRandomData(length, min, max, multiplier = 10, maxDiff = 10) {
  var array = new Array(length).fill();
  let lastValue;

  return array.map((item, index) => {
    let randomValue = Math.floor(Math.random() * multiplier + 1);

    while (
      randomValue <= min ||
      randomValue >= max ||
      (lastValue && randomValue - lastValue > maxDiff)
    ) {
      randomValue = Math.floor(Math.random() * multiplier + 1);
    }

    lastValue = randomValue;

    return { value: randomValue };
  });
}

function getMainChartData() {
  var resultArray = [];
  var stats = [31.7,52.4,15.9,0];
  let level = ["Informational","Low","Medium","High"]
  for (let i = 0; i < level.length; i++) {
    resultArray.push({
      stats: stats[i],
